import React from 'react'
import {useForm,useStep} from "react-hooks-helper"
import { NameAndPhone } from './steps/NameAndPhone'
import { EmailAndAddress } from './steps/EmailAndAddress'
import { Confirmation } from './steps/Confirmation'


const defaultData = {
    fistName:"",
    lastName:"",
    phoneNumber:"",
    email:"",
    dob:""

}

const steps = [{id:"NameAndPhone"},
{id:"EmailAndAddress"},
{id:"Confirmation"},
{id:"submit"}]

export const RegisterForm = () => {
    const [formData,setForm] = useForm(defaultData)
    const {step, navigation} = useStep({
        steps,
        initialStep:1
    })

    switch(step.id){
        case "NameAndPhone":
            return <NameAndPhone/>;
        case "EmailAndAddress":
            return <EmailAndAddress/>;
        case "Confirmation":
            return <Confirmation/>;

    }

    return (
        <div>
            <h1>RegisterForm</h1>
        </div>
    )
}
