import React from 'react'
import Container from "@material-ui/core/Container"
import Accordion from "@material-ui/core/Accordion"
import IconButton from "@material-ui/core/IconButton"
import EditIcon from "@material-ui/icons/Edit"
import AccordionSummary from "@material-ui/core/AccordionSummary"
import AccordionDetail from "@material-ui/core/AccordionDetails"
import ListItemText from "@material-ui/core/ListItemText"





export const Confirmation = (data) => {
    console.log(data)
    return (
      <Container maxWidth="sm" >
          <h1>Confirmation</h1>
          <RenderAccordion summary="NameAndPhone" 
          details={[{"First name":data.firstName},
          {"Last name":data.lastName},
          {"Phone number":data.phone}
          ]}/>
             <RenderAccordion summary="EmailAndDob" 
          details={[{"E-email":data.email},
          {"Date of birth":data.dob}
          ]}/>
      </Container>
    )
}


export const RenderAccordion = ({summary,details}) => (
    <Accordion>
        <AccordionSummary>
          {summary}
        </AccordionSummary>
        <AccordionDetail>
            <div>
            {details.map((data,index)=>{
              const keys = Object.keys(data)[0];
              const values = data[Object.keys(data)[0]]

              return <ListItemText key={index}>{`${keys}: ${values}`}</ListItemText>

             })}
             <IconButton
             color="primary"
             component="span"
             >
                  <EditIcon/>
             </IconButton>

            </div>
        </AccordionDetail>
    </Accordion>
)
