import React,{useState,useEffect} from 'react'
import {useForm,useStep} from "react-hooks-helper"
import { NameAndPhone } from './steps/NameAndPhone'
import { EmailAndDob } from './steps/EmailAndDob'
import { Confirmation } from './steps/Confirmation'
import { Submit } from './steps/Submit'



const defaultData = {
    fistName:"",
    lastName:"",
    phoneNumber:"",
    email:"",
    dob:""

}
const steps = [{id:"NameAndPhone"},
{id:"EmailAndDob"},
{id:"Confirmation"},
{id:"Submit"}]

export const RegisterForm = () => {
    const [values, setValues] = useState(defaultData);
    //const [data,setForm] = useForm(defaultData)
    const {step, navigation} = useStep({
        steps,
        initialStep:0
    })

    const handleFirstNameInputChange = (event) => {
	//event.persist();
	setValues((values) => ({
		...values,
		firstName: event.target.value,
	}));
}

   const handleLastNameInputChange = (event) => {
	event.persist();
	setValues((values) => ({
		...values,
		lastName: event.target.value,
	}));
}

   const handlePhoneNumberInputChange = (event) => {
	event.persist();
	setValues((values) => ({
		...values,
		phoneNumber: event.target.value,
	}));
}

   const handleEmailInputChange = (event) => {
	event.persist();
	setValues((values) => ({
		...values,
		email: event.target.value,
	}));
}
 const handleDobInputChange = (event) => {
	event.persist();
	setValues((values) => ({
		...values,
		dob: event.target.value,
	}));
}


    const props = {
      handleDobInputChange, handleEmailInputChange, handlePhoneNumberInputChange,handleFirstNameInputChange,handleLastNameInputChange,navigation
    }


    switch(step.id){
        case "NameAndPhone":
            return <NameAndPhone  data = {props}/>;
        case "EmailAndDob":
            return <EmailAndDob  data = {props}/>;
        case "Confirmation":
            return <Confirmation dataValues={values} data = {props}/>;
        case "Submit":
            return <Submit {...props}/>;
        default:
            console.log("empty value")
    }

    return (
        <div>
            <h1>RegisterForm</h1>
        </div>
    )
}
