import React from 'react'
import {useForm,useStep} from "react-hooks-helper"
import { NameAndPhone } from './steps/NameAndPhone'
import { EmailAndDob } from './steps/EmailAndDob'
import { Confirmation } from './steps/Confirmation'
import { Submit } from './steps/Submit'



const defaultData = {
    fistName:"",
    lastName:"",
    phoneNumber:"",
    email:"",
    dob:""

}
const steps = [{id:"NameAndPhone"},
{id:"EmailAndDob"},
{id:"Confirmation"},
{id:"Submit"}]

export const RegisterForm = () => {
    const [data,setForm] = useForm({
    fistName:"",
    lastName:"",
    phoneNumber:"",
    email:"",
    dob:""

})
    const {step, navigation} = useStep({
        steps,
        initialStep:0
    })
    const props = {
        data,setForm,navigation
    }
console.log(data.firstName)
console.log(data.lastName)
console.log(data.phoneNumber)

    switch(step.id){
        case "NameAndPhone":
            return <NameAndPhone  firstName = {props.data.fistName}/>;
        case "EmailAndDob":
            return <EmailAndDob {...props}/>;
        case "Confirmation":
            return <Confirmation {...props}/>;
        case "Submit":
            return <Submit {...props}/>;
        default:
            console.log("empty value")
    }

    return (
        <div>
            <h1>RegisterForm</h1>
        </div>
    )
}
