import React from 'react'
import { NameAndPhone } from './steps/NameAndPhone'
import { EmailAndDob } from './steps/EmailAndDob'
import { Confirmation } from './steps/Confirmation'
import { Submit } from './steps/Submit'


export  class RegisterForm extends React.Component  {
           state = {
                errors: {},
               step:1,
                fields: {
               firstName:"",
               lastName:"",
               email:"",
               phoneNumber:"",
               dob:""}
           }
           ///////////////
           handleValidation(){
            let fields = this.state.fields;
            let errors = {};
            let formIsValid = true;
            let counter = this.state.step

            //FirstName
            if(!fields["firstName"]){
               formIsValid = false;
               errors["firstNameEr"] = "first name Cannot be empty";
            }

            if(typeof fields["firstName"] !== "undefined"){
               if(!fields["firstName"].match(/^[a-zA-Z]+$/)){
                  formIsValid = false;
                  errors["firstNameEr"] = "first name Only letters";
               }        
            }
       //lastName
           if(!fields["lastName"]){
               formIsValid = false;
               errors["lastNameEr"] = "last name Cannot be empty";
            }

            if(typeof fields["lastName"] !== "undefined"){
               if(!fields["lastName"].match(/^[a-zA-Z]+$/)){
                  formIsValid = false;
                  errors["lastNameEr"] = "last Only letters";
               }        
            }
        //phone number
           if(!fields["phoneNumber"]){
               formIsValid = false;
               errors["phoneNumberEr"] = "phone number Cannot be empty";
            }

            if(typeof fields["phoneNumber"] !== "undefined"){
               if(!fields["phoneNumber"].match(/^[a-zA-Z]+$/)){
                  formIsValid = false;
                  errors["phoneNumberEr"] = "phone number Only numbers";
               }        
            }
            if(counter==2) {
       //Email
            if(!fields["email"]){
               formIsValid = false;
               errors["emailEr"] = "email Cannot be empty";
            }

            if(typeof fields["email"] !== "undefined"){
               let lastAtPos = fields["email"].lastIndexOf('@');
               let lastDotPos = fields["email"].lastIndexOf('.');

               if (!(lastAtPos < lastDotPos && lastAtPos > 0 && fields["email"].indexOf('@@') == -1 && lastDotPos > 2 && (fields["email"].length - lastDotPos) > 2)) {
                  formIsValid = false;
                  errors["emailEr"] = "Email is not valid";
                }
           }  

            }

           this.setState({errors: errors});
           return formIsValid;
       }

           /////////////////

           nextStep = () => {
               const {firstName,lastName,phoneNumber,error} = this.state.fields;
               const {step} =  this.state
                   if(this.handleValidation()){
                              this.setState({
                                    step:step+1
                                })

            }
            //    if(firstName.length==0 ||typeof parseInt(firstName) == Number) {
            //       if(!error.length) {
            //       this.setState({
            //        error:"Pleas enter your first name"
            //      })
            //       }else {
            //        this.setState({
            //        error:""
            //      })
            //       }

            //    }else if(lastName.length==0 ) {
            //          alert("Pleas enter your last name")

            //    }else if(phoneNumber.length==0){
            //            alert("Pleas enter your phone number")
            //    } else {
            //             this.setState({
            //        step:step+1
            //    })
              // }

           }
            previousStep = () => {
               const {step} = this.state;
               this.setState({
                   step:step-1
               })
           }

    handleChange =(field)=> (e)=>{         
            let fields = this.state.fields;
            fields[field] = e.target.value;        
            this.setState({fields});
        }
        //    handleChange =(input)=> (e)=>{
        //      this.setState({
        //          [input]:e.target.value
        //      })
        //    }

    render () {
        const {step,errors} = this.state
        const {firstName,lastName,email,phoneNumber,dob} = this.state.fields
        const values = {firstName,lastName,email,phoneNumber,dob,errors}


        switch(step) {
            case 1:
                return(
                    <NameAndPhone 
                        nextStep ={this.nextStep}
                        handleChange={this.handleChange}
                        values={values}
                    />
                )
            case 2:
              return(
                    <EmailAndDob
                       nextStep ={this.nextStep}
                       previousStep={this.previousStep}
                        handleChange={this.handleChange}
                        values={values}

                    />
              )
              case 3:
                  return (
                      <Confirmation
                        nextStep ={this.nextStep}
                        previousStep={this.previousStep}
                        values={values}
                      />
                  )
              case 4:
                  return (
                      <Submit/>
                  )

        }
        }

}
export default RegisterForm;
