//     dob:""

// }
const steps = [{id:"NameAndPhone"},
{id:"EmailAndDob"},
{id:"Confirmation"},
{id:"Submit"}]

export const RegisterForm = () => {
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [phoneNumber, setPhoneNumber] = useState('');
    const [email, setEmail] = useState('');
    const [dob, setDob] = useState('');

  const defaultData = {
    firstName,
    lastName,
   phoneNumber,
   email,
   dob

}

    const [data,setForm] = useForm(defaultData)


    const {step, navigation} = useStep({
        steps,
        initialStep:0
    })
    const props = {
        data,setForm,navigation
    }
console.log(data.firstName)
console.log(data.lastName)
console.log(data.phoneNumber)

    switch(step.id){
        case "NameAndPhone":
            return <NameAndPhone  data = {props}/>;
        case "EmailAndDob":
            return <EmailAndDob {...props}/>;
        case "Confirmation":
            return <Confirmation {...props}/>;
        case "Submit":
            return <Submit {...props}/>;
        default:
            console.log("empty value")
    }

    return (
        <div>
            <h1>RegisterForm</h1>
        </div>
    )
}
