import React from 'react'
import { NameAndPhone } from './steps/NameAndPhone'
import { EmailAndDob } from './steps/EmailAndDob'
import { Confirmation } from './steps/Confirmation'
import { Submit } from './steps/Submit'


const validEmailRegex = 
  RegExp(/^(([^<>()\[\]\.,;:\s@\"]+(\.[^<>()\[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i);
export  class RegisterForm extends React.Component  {
           state = {
                errors: {
                firstName: '',
                lastName:'',
                email: '',
                phoneNumber: '',
                dob:''},
               step:1,
               firstName:"",
               lastName:"",
               email:"",
               phoneNumber:"",
               dob:""
           }

           nextStep = () => {
               const {step,firstName,lastName,phoneNumber,error} = this.state;
            //    if(firstName.length==0 ||typeof parseInt(firstName) == Number) {
            //       if(!error.length) {
            //       this.setState({
            //        error:"Pleas enter your first name"
            //      })
            //       }else {
            //        this.setState({
            //        error:""
            //      })
            //       }

            //    }else if(lastName.length==0 ) {
            //          alert("Pleas enter your last name")

            //    }else if(phoneNumber.length==0){
            //            alert("Pleas enter your phone number")
            //    } else {
                        this.setState({
                   step:step+1
               })
             //  }

           }
            previousStep = () => {
               const {step} = this.state;
               this.setState({
                   step:step-1
               })
           }

           handleChange =(input)=> (e)=>{
              const { name, value } = e.target;
              let errors = this.state.errors;
                switch (name) {
                    case 'firstName': 
                    errors.firstName = 
                        value.length < 5
                        ? 'First Name must be 5 characters long!'
                        : '';
                    break;
                    case 'email': 
                    errors.email = 
                        validEmailRegex.test(value)
                        ? ''
                        : 'Email is not valid!';
                    break;
                    case 'password': 
                    errors.password = 
                        value.length < 8
                        ? 'Password must be 8 characters long!'
                        : '';
                    break;
                    default:
                    break;
                }
                  this.setState({errors, [name]: value}, ()=> {
                        console.log(errors)
                    })

            //  this.setState({
            //      [input]:e.target.value
            //  })
           }

    render () {
        const {step} = this.state
        const {firstName,lastName,email,phoneNumber,dob,error} = this.state
        const values = {firstName,lastName,email,phoneNumber,dob,error}

        switch(step) {
            case 1:
                return(
                    <NameAndPhone 
                        nextStep ={this.nextStep}
                        handleChange={this.handleChange}
                        values={values}
                    />
                )
            case 2:
              return(
                    <EmailAndDob
                       nextStep ={this.nextStep}
                       previousStep={this.previousStep}
                        handleChange={this.handleChange}
                        values={values}

                    />
              )
              case 3:
                  return (
                      <Confirmation
                        nextStep ={this.nextStep}
                        previousStep={this.previousStep}
                        values={values}
                      />
                  )
              case 4:
                  return (
                      <Submit/>
                  )

        }
        }

}
export default RegisterForm;
